    private void hitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungActionPerformed
        // TODO add your handling code here:
        if (nama.getText().equals("")||bb.getText().equals("")||tinggi.getText().equals(""))
        {
            JOptionpane.showMessageDialog(null, "Kotak isian harus diisi");
        }else if (perempuan.isSelected()||laki.isSelected())
        {
            try {
                proses();
            }catch (Exception e){
                JOptionpane.showMessageDialog(null, "Masukan ada yang salah");
            }
        } else {
            JOptionpane.showMessageDialog(null, "Radio button harus dipilih");
        }
        
    }//GEN-LAST:event_hitungActionPerformed

