    private void proses() 
    {
        tinggibdn = Double.parseDouble(tinggi.getText());
        berat = Double.parseDouble(bb.getText());
        
        if (perempuan.isSelected())
        {
            hasil = (tinggibdn - 110) * 1;
        } else if (laki.isSelected())
        {
            hasil = (tinggibdn - 100) * 1;
        }
        temp = Double.toString(hasil.intValue());
        
        if (hasil<berat){
            ideal.setText(temp);
            hsl.setText("Maaf " + nama.getText() + " ,Sepertinya anda Overweight : ( ");
            saran.setText("Banyaklah berolahraga");
        }
        else if (hasil > berat)
        {
            ideal.setText(temp);
            hsl.setText("Maaf " + nama.getText() + " ,Sepertinya anda Underweight : ( ");
            saran.setText("Banyak makan makanan bergizi dan berkarbohidrat");
        }
        else
        {
            ideal.setText(temp);
            hsl.setText("Hallo " + nama.getText() + " ,Berat badan ideal : ( ");
            saran.setText("lanjutkan polamakan dan gaya hidup sehat");
        }
    }
